// 实验证明，本题只需要把所有值相等的情形处理好就可以了
// 不需要随机选pivot，选第一个就可
// 另外，随机比每次选第一个的优势是可以处理2，。。。2,3，。。。3的情形
// 但，就算随机，仍旧不能很好的处理2，。。。2，3的情形
// 20140521
/**
 * Definition for singly-linked list.
 */
#include <algorithm>
#include <iostream>
using namespace std;
int fail = 0;
int loop = 0;
struct ListNode {
	int val;
	ListNode *next;
	ListNode() : val(0), next(0) {}
	ListNode(int x, ListNode *n) : val(x), next(n) {}
	// ListNode(int x) : val(x), next(0) {}
};
class Solution3 {
public:
    ListNode *sortList(ListNode *head) {
        return quicksort(head, NULL);
    }
    ListNode *quicksort(ListNode *head, ListNode *tail) {
        if (!head) return NULL;
        if (head -> next == tail) return head;
        ListNode *nexthead = head;
        while (nexthead -> next && nexthead -> next -> val == head -> val) nexthead = nexthead -> next;
        ListNode *prehead = head, *posthead = nexthead, *p = nexthead -> next;
        while (p != tail) {
            if (p -> val < head -> val) {
                ListNode *tmp1 = p -> next;
                ListNode *tmp2 = prehead;
                prehead = p;
                prehead -> next = tmp2;
                p = tmp1;
            } else {
                posthead -> next = p;
                posthead = p;
                p = p -> next;
            }
        }
        posthead -> next = tail;
        if (nexthead -> next != tail) nexthead -> next = quicksort(nexthead -> next, tail);
        if (prehead == head) return head;
        else
			return quicksort(prehead, head);
    }
};

class Solution {
public:
int listLength(ListNode *head){
	int len = 0;
	while(head){
		++len;
		head = head->next;
	}
	return len;
}

    ListNode *sortList(ListNode *head) {
		if(head == 0 || head->next == 0) return head;
		int pivot_idx = rand()%listLength(head);
		// pivot_idx = 0;
		int pitmp = pivot_idx;

		ListNode * pivot = head;
		while(pivot_idx--) pivot = pivot->next;
		// if(pivot_idx+1 != pitmp) cout<<"DDDDD"<<endl;
		int midItem = pivot->val;

		ListNode * _head = head;
		ListNode * last = head;
		ListNode * bf_last=last;
		bool necessary = false;

		int last_pos = 0;
		while(_head != 0){

			if(_head->val != midItem) necessary = true;
			if(_head->val < midItem){
				bf_last = last;
				if(_head != last)
				swap(_head->val, last->val);
				last = last->next;
				++last_pos;
			}
			_head = _head->next;
		}
		if(last_pos < pitmp)
		swap(pivot->val, last->val);
		++loop;
		if(!necessary) return head;
		if(bf_last == last){
			++fail;
			head->next = sortList(head->next);
			return head;
		}else{
			bf_last->next = 0;
			sortList(head);
			bf_last->next = last;
			if(last_pos < pitmp)
				sortList(last->next);
			else
				sortList(last);


			return head;
		}
		
    }
};
